mapper.select:
select  clazz_name
from nur_stuinfo ns
where 1=1
 and grade=#{grade}
and academic_year=#{classYear}
  and rounds=#{lunci}
 and schid =#{schId}
group by clazz_name

select  rowkey,cla_shortname
from nur_class
where 1=1

  and cla_schId =#{schId}


  and cla_nameyear=#{claYear}


  and cla_level=#{claLevel}

group by cla_name

 select  rowkey,cla_nameyear as claNameYear
 from nur_class
 where 1=1

     and cla_schId =#{schId}


     and cla_nameyear=#{claYear}


     and cla_level=#{claLevel}

 group by cla_nameyear
order by cla_nameyear  desc

select  rowkey,cla_nameyear as claNameYear
from nur_class
where 1=1

   and  cla_schId in

        #{id}



    and cla_nameyear=#{claYear}


    and   cla_level in

        #{id}


order by cla_nameyear  desc
